import React, { useState, useEffect } from 'react';
import '../styles/pages/AdminDashboard.css';

const AdminDashboard = () => {
  const [resources, setResources] = useState([]);
  const [newResource, setNewResource] = useState({
    title: '',
    description: '',
    pricing: '',
    status: 'active',
  });
  const [error, setError] = useState(null);

  // Fetch resources from the backend
  useEffect(() => {
    const fetchResources = async () => {
      try {
        const response = await fetch('/api/resource'); // Adjust endpoint
        if (!response.ok) {
          throw new Error('Failed to fetch resources');
        }
        const data = await response.json();
        setResources(data);
      } catch (err) {
        console.error('Error fetching resources:', err.message);
        setError(err.message);
      }
    };

    fetchResources();
  }, []);

  // Handle new resource submission
  const handleNewResourceSubmit = async (e) => {
    e.preventDefault();
    setError(null); // Reset error before submission
    try {
      const response = await fetch('/api/resource', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newResource),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to create resource');
      }

      const createdResource = await response.json();
      setResources((prevResources) => [...prevResources, createdResource]); // Add to state
      setNewResource({ title: '', description: '', pricing: '', status: 'active' }); // Reset form
    } catch (err) {
      console.error('Error creating resource:', err.message);
      setError(err.message);
    }
  };

  return (
    <div className="admin-dashboard">
      <header className="dashboard-header">
        <h1>Admin Dashboard</h1>
        <p>Manage resources and jobs here.</p>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </header>

      {/* Resource Creation Section */}
      <section className="resource-creation">
        <h2>Create New Resource</h2>
        <form onSubmit={handleNewResourceSubmit}>
          <label>
            Title:
            <input
              type="text"
              value={newResource.title}
              onChange={(e) =>
                setNewResource({ ...newResource, title: e.target.value })
              }
              required
            />
          </label>
          <label>
            Description:
            <textarea
              value={newResource.description}
              onChange={(e) =>
                setNewResource({ ...newResource, description: e.target.value })
              }
              required
            ></textarea>
          </label>
          <label>
            Pricing:
            <input
              type="number"
              value={newResource.pricing}
              onChange={(e) =>
                setNewResource({ ...newResource, pricing: e.target.value })
              }
              required
            />
          </label>
          <label>
            Status:
            <select
              value={newResource.status}
              onChange={(e) =>
                setNewResource({ ...newResource, status: e.target.value })
              }
            >
              <option value="active">Active</option>
              <option value="completed">Completed</option>
              <option value="pending">Pending</option>
            </select>
          </label>
          <button type="submit">Create Resource</button>
        </form>
      </section>

      {/* Resource Management Section */}
      <section className="resource-management">
        <h2>Manage Resources</h2>
        <table className="resource-table">
          <thead>
            <tr>
              <th>Resource ID</th>
              <th>Title</th>
              <th>Description</th>
              <th>Pricing</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {resources.map((resource) => (
              <tr key={resource._id}>
                <td>{resource._id}</td>
                <td>{resource.title}</td>
                <td>{resource.description}</td>
                <td>{resource.pricing}</td>
                <td>{resource.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
};

export default AdminDashboard;
