import React, { useState, useEffect } from 'react';
import '../styles/pages/AdminDashboard.css';

const AdminDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [newJob, setNewJob] = useState({ title: '', status: 'active' });
  const [editingJob, setEditingJob] = useState(null);
  const [error, setError] = useState(null);

  // Fetch jobs from the backend
  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const response = await fetch('/api/jobs'); // Replace with your backend URL
        if (!response.ok) {
          throw new Error('Failed to fetch jobs');
        }
        const data = await response.json();
        setJobs(data);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchJobs();
  }, []);

  // Handle new job submission
  const handleNewJobSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/jobs', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newJob),
      });

      if (!response.ok) {
        throw new Error('Failed to create job');
      }

      const createdJob = await response.json();
      setJobs((prevJobs) => [...prevJobs, createdJob]);
      setNewJob({ title: '', status: 'active' }); // Reset the form
    } catch (err) {
      console.error('Error creating job:', err.message);
      setError(err.message);
    }
  };

  // Handle job editing
  const handleEdit = (job) => {
    setEditingJob(job);
  };

  // Handle job saving (update)
  const handleSave = async () => {
    try {
      const response = await fetch(`/api/jobs/${editingJob._id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editingJob),
      });

      if (!response.ok) {
        throw new Error('Failed to update job');
      }

      const updatedJob = await response.json();
      setJobs((prevJobs) =>
        prevJobs.map((job) =>
          job._id === updatedJob._id ? updatedJob : job
        )
      );
      setEditingJob(null); // Close the modal
    } catch (err) {
      console.error('Error updating job:', err.message);
      setError(err.message);
    }
  };

  // Handle job deletion
  const handleDelete = async (jobId) => {
    try {
      const response = await fetch(`/api/jobs/${jobId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to delete job');
      }

      setJobs((prevJobs) => prevJobs.filter((job) => job._id !== jobId));
    } catch (err) {
      console.error('Error deleting job:', err.message);
      setError(err.message);
    }
  };

  return (
    <div className="admin-dashboard">
      <header className="dashboard-header">
        <h1>Admin Dashboard</h1>
        <p>Manage jobs, users, and platform performance here.</p>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </header>

      {/* Job Creation Section */}
      <section className="job-creation">
        <h2>Create New Job</h2>
        <form onSubmit={handleNewJobSubmit}>
          <label>
            Title:
            <input
              type="text"
              value={newJob.title}
              onChange={(e) => setNewJob({ ...newJob, title: e.target.value })}
              required
            />
          </label>
          <label>
            Status:
            <select
              value={newJob.status}
              onChange={(e) => setNewJob({ ...newJob, status: e.target.value })}
            >
              <option value="active">Active</option>
              <option value="completed">Completed</option>
              <option value="pending">Pending</option>
            </select>
          </label>
          <button type="submit">Create Job</button>
        </form>
      </section>

      {/* Job Management Section */}
      <section className="job-management">
        <h2>Manage Jobs</h2>
        <table className="job-table">
          <thead>
            <tr>
              <th>Job ID</th>
              <th>Title</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {jobs.map((job) => (
              <tr key={job._id}>
                <td>{job._id}</td>
                <td>{job.title}</td>
                <td>{job.status}</td>
                <td>
                  <button onClick={() => handleEdit(job)}>Edit</button>
                  <button onClick={() => handleDelete(job._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* Edit Job Modal */}
      {editingJob && (
        <div className="edit-modal">
          <div className="modal-content">
            <h2>Edit Job</h2>
            <form>
              <label>
                Title:
                <input
                  type="text"
                  value={editingJob.title}
                  onChange={(e) =>
                    setEditingJob({ ...editingJob, title: e.target.value })
                  }
                />
              </label>
              <label>
                Status:
                <select
                  value={editingJob.status}
                  onChange={(e) =>
                    setEditingJob({ ...editingJob, status: e.target.value })
                  }
                >
                  <option value="active">Active</option>
                  <option value="completed">Completed</option>
                  <option value="pending">Pending</option>
                </select>
              </label>
              <button type="button" onClick={handleSave}>
                Save
              </button>
              <button type="button" onClick={() => setEditingJob(null)}>
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
