import React, { useState } from 'react';
import '../styles/pages/JobListings.css'; // Link the CSS file for styling

const JobListings = () => {
  // State to hold job data and filters
  const [jobs, setJobs] = useState([]);
  const [filters, setFilters] = useState({
    status: 'all',
    search: '',
    minBudget: '',
    maxBudget: '',
    sortBy: 'date', // or 'price'
  });

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    budget: '',
    deadline: '',
    status: 'pending', // Default status for new jobs
  });

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Add a new job
  const handleAddJob = (e) => {
    e.preventDefault();
    if (formData.title && formData.description) {
      const newJob = { ...formData, id: Date.now() };
      setJobs([...jobs, newJob]);
      setFormData({
        title: '',
        description: '',
        budget: '',
        deadline: '',
        status: 'pending',
      });
    }
  };

  // Handle filter changes
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  // Filter and sort jobs based on filters
  const filteredJobs = jobs
    .filter((job) => {
      // Filter by status
      if (filters.status !== 'all' && job.status !== filters.status) {
        return false;
      }
      // Filter by search term
      if (
        filters.search &&
        !job.title.toLowerCase().includes(filters.search.toLowerCase())
      ) {
        return false;
      }
      // Filter by budget range
      if (
        (filters.minBudget && job.budget < filters.minBudget) ||
        (filters.maxBudget && job.budget > filters.maxBudget)
      ) {
        return false;
      }
      return true;
    })
    .sort((a, b) => {
      // Sort by date or price
      if (filters.sortBy === 'date') {
        return new Date(a.deadline) - new Date(b.deadline);
      }
      if (filters.sortBy === 'price') {
        return a.budget - b.budget;
      }
      return 0;
    });

  return (
    <div className="job-listings-page">
      <header className="job-listings-header">
        <h1>Job Listings</h1>
        <p>Post and manage your job opportunities here.</p>
      </header>
      <main className="job-listings-content">
        <section className="add-job-section">
          <h2>Add a New Job</h2>
          <form onSubmit={handleAddJob} className="add-job-form">
            <div className="form-group">
              <label htmlFor="title">Job Title</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="description">Job Description</label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                required
              ></textarea>
            </div>
            <div className="form-group">
              <label htmlFor="budget">Budget</label>
              <input
                type="number"
                id="budget"
                name="budget"
                value={formData.budget}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="deadline">Deadline</label>
              <input
                type="date"
                id="deadline"
                name="deadline"
                value={formData.deadline}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="status">Status</label>
              <select
                id="status"
                name="status"
                value={formData.status}
                onChange={handleInputChange}
              >
                <option value="pending">Pending</option>
                <option value="completed">Completed</option>
              </select>
            </div>
            <button type="submit" className="add-job-button">
              Add Job
            </button>
          </form>
        </section>

        {/* Filters Section */}
        <section className="job-filters">
          <h2>Filter Jobs</h2>
          <div className="filters">
            <input
              type="text"
              placeholder="Search by title"
              name="search"
              value={filters.search}
              onChange={handleFilterChange}
            />
            <select
              name="status"
              value={filters.status}
              onChange={handleFilterChange}
            >
              <option value="all">All</option>
              <option value="pending">Pending</option>
              <option value="completed">Completed</option>
            </select>
            <input
              type="number"
              placeholder="Min Budget"
              name="minBudget"
              value={filters.minBudget}
              onChange={handleFilterChange}
            />
            <input
              type="number"
              placeholder="Max Budget"
              name="maxBudget"
              onChange={handleFilterChange}
            />
            <select
              name="sortBy"
              value={filters.sortBy}
              onChange={handleFilterChange}
            >
              <option value="date">Sort by Date</option>
              <option value="price">Sort by Price</option>
            </select>
          </div>
        </section>

        {/* Job Listings Section */}
        <section className="job-listings-section">
          <h2>Available Jobs</h2>
          {filteredJobs.length > 0 ? (
            <ul className="job-listings">
              {filteredJobs.map((job) => (
                <li key={job.id} className="job-card">
                  <h3>{job.title}</h3>
                  <p>{job.description}</p>
                  <p>
                    <strong>Budget:</strong> ${job.budget || 'N/A'}
                  </p>
                  <p>
                    <strong>Deadline:</strong> {job.deadline || 'N/A'}
                  </p>
                  <p>
                    <strong>Status:</strong> {job.status}
                  </p>
                </li>
              ))}
            </ul>
          ) : (
            <p>No jobs match your filters.</p>
          )}
        </section>
      </main>
    </div>
  );
};

export default JobListings;
