import React, { useState } from 'react';
import '../styles/pages/JobListings.css'; // Link the CSS file for styling

const JobListings = ({ jobs, fetchJobs }) => {
  const [showForm, setShowForm] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    budget: '',
    deadline: '',
    status: 'active',
  });
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  // Handle "Edit Job" button click
  const handleEditClick = (job) => {
    setSelectedJob(job);
    setFormData({ ...job });
    setEditMode(true);
    setShowForm(true);
    setSuccessMessage('');
    setError('');
  };

  // Handle "Delete Job" button click
  const handleDeleteClick = async (jobId) => {
    try {
      const response = await fetch(`/api/resource/${jobId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to delete job');
      }

      setSuccessMessage('Job deleted successfully!');
      fetchJobs(); // Refresh jobs
    } catch (err) {
      console.error('Error deleting job:', err.message);
      setError(err.message);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission for creating or updating a job
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const method = editMode ? 'PUT' : 'POST';
      const url = editMode ? `/api/resource/${selectedJob._id}` : '/api/resource';
      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to save job');
      }

      setSuccessMessage(editMode ? 'Job updated successfully!' : 'Job created successfully!');
      setShowForm(false);
      setEditMode(false);
      fetchJobs(); // Refresh jobs
    } catch (err) {
      console.error('Error saving job:', err.message);
      setError(err.message);
    }
  };

  return (
    <div className="job-listings">
      <h2>Job Listings</h2>
      {successMessage && <p className="success-message">{successMessage}</p>}
      {error && <p className="error-message">{error}</p>}

      {jobs.length > 0 ? (
        <ul className="job-listings">
          {jobs.map((job) => (
            <li key={job._id} className="job-card">
              <h3>{job.title}</h3>
              <p>{job.description}</p>
              <p><strong>Budget:</strong> ${job.budget}</p>
              <p><strong>Deadline:</strong> {job.deadline || 'N/A'}</p>
              <p><strong>Status:</strong> {job.status}</p>
              <div className="job-actions">
                <button className="edit-button" onClick={() => handleEditClick(job)}>
                  Edit
                </button>
                <button className="delete-button" onClick={() => handleDeleteClick(job._id)}>
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No jobs available at the moment.</p>
      )}

      {showForm && (
        <div className="job-form-modal">
          <div className="job-form">
            <h3>{editMode ? 'Edit Job' : 'Create Job'}</h3>
            <form onSubmit={handleFormSubmit}>
              <div className="form-group">
                <label htmlFor="title">Title</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea
                  id="description"
                  name="description"
                  value={formData.description}
                  onChange={handleInputChange}
                  required
                ></textarea>
              </div>
              <div className="form-group">
                <label htmlFor="budget">Budget</label>
                <input
                  type="number"
                  id="budget"
                  name="budget"
                  value={formData.budget}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="deadline">Deadline</label>
                <input
                  type="date"
                  id="deadline"
                  name="deadline"
                  value={formData.deadline}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label htmlFor="status">Status</label>
                <select
                  id="status"
                  name="status"
                  value={formData.status}
                  onChange={handleInputChange}
                  required
                >
                  <option value="active">Active</option>
                  <option value="completed">Completed</option>
                  <option value="pending">Pending</option>
                </select>
              </div>
              <button type="submit" className="submit-button">
                {editMode ? 'Save Changes' : 'Create Job'}
              </button>
              <button type="button" className="cancel-button" onClick={() => setShowForm(false)}>
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default JobListings;
