import React, { useState, useEffect } from 'react';
import '../styles/pages/AdminDashboard.css';

const AdminDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [newJob, setNewJob] = useState({
    title: '',
    description: '',
    pricing: '',
    status: 'active',
  });
  const [error, setError] = useState(null);

  // Fetch jobs from the backend (saved under /api/resource)
  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const response = await fetch('/api/resource'); // Using /api/resource for jobs
        if (!response.ok) {
          throw new Error(`Failed to fetch jobs: ${response.status} ${response.statusText}`);
        }
        const data = await response.json();
        setJobs(data); // Treat resources as jobs in the frontend
      } catch (err) {
        console.error('Error fetching jobs:', err.message);
        setError(err.message);
      }
    };

    fetchJobs();
  }, []);

  // Handle new job submission
  const handleNewJobSubmit = async (e) => {
    e.preventDefault();
    setError(null); // Reset error before submission
    try {
      console.log('Submitting new job:', newJob);
      const response = await fetch('/api/resource', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newJob), // Sending job details to /api/resource
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || `Failed to create job: ${response.status} ${response.statusText}`);
      }

      const createdJob = await response.json();
      console.log('Created Job:', createdJob);
      setJobs((prevJobs) => [...prevJobs, createdJob]); // Add the created job to the frontend list
      setNewJob({ title: '', description: '', pricing: '', status: 'active' }); // Reset form
    } catch (err) {
      console.error('Error creating job:', err.message);
      setError(err.message);
    }
  };

  return (
    <div className="admin-dashboard">
      <header className="dashboard-header">
        <h1>Admin Dashboard</h1>
        <p>Manage jobs here.</p>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </header>

      {/* Job Creation Section */}
      <section className="job-creation">
        <h2>Create New Job</h2>
        <form onSubmit={handleNewJobSubmit}>
          <label>
            Title:
            <input
              type="text"
              value={newJob.title}
              onChange={(e) =>
                setNewJob({ ...newJob, title: e.target.value })
              }
              required
            />
          </label>
          <label>
            Description:
            <textarea
              value={newJob.description}
              onChange={(e) =>
                setNewJob({ ...newJob, description: e.target.value })
              }
              required
            ></textarea>
          </label>
          <label>
            Pricing:
            <input
              type="number"
              value={newJob.pricing}
              onChange={(e) =>
                setNewJob({ ...newJob, pricing: Number(e.target.value) })
              }
              required
            />
          </label>
          <label>
            Status:
            <select
              value={newJob.status}
              onChange={(e) => setNewJob({ ...newJob, status: e.target.value })}
            >
              <option value="active">Active</option>
              <option value="completed">Completed</option>
              <option value="pending">Pending</option>
            </select>
          </label>
          <button type="submit">Create Job</button>
        </form>
      </section>

      {/* Job Management Section */}
      <section className="job-management">
        <h2>Manage Jobs</h2>
        <table className="job-table">
          <thead>
            <tr>
              <th>Job ID</th>
              <th>Title</th>
              <th>Description</th>
              <th>Pricing</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {jobs.map((job) => (
              <tr key={job._id}>
                <td>{job._id}</td>
                <td>{job.title}</td>
                <td>{job.description}</td>
                <td>{job.pricing}</td>
                <td>{job.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
};

export default AdminDashboard;
